/**
 * Classe Actor minimale pour le système The Witcher (FR)
 * Étend la classe Actor de Foundry et prépare les données système de base.
 */
class TRPGActor extends Actor {
  prepareData() {
    super.prepareData();
    // Préparez ici les données spécifiques au système (ex: resources, caractéristiques)
    const actorData = this.system || this.data?.data || {};
    actorData.resources = actorData.resources || { health: { value: 0, max: 0 }, power: { value: 0, max: 0 } };
  }
}

/**
 * Classe Item minimale pour le système The Witcher (FR)
 */
class TRPGItem extends Item {
  prepareData() {
    super.prepareData();
    const itemData = this.system || this.data?.data || {};
    itemData.description = itemData.description || "";
  }
}

// Exposer globalement pour que TRPG_TW_FR.mjs puisse les référencer lors de l'init
window.TRPGActor = TRPGActor;
window.TRPGItem = TRPGItem;

export { TRPGActor, TRPGItem };

/**
 * Feuille d'acteur minimale pour The Witcher (FR)
 */
class TRPGActorSheet extends ActorSheet {
  static get defaultOptions() {
    return mergeObject(super.defaultOptions, {
      classes: ["trpg-tw-fr", "sheet", "actor"],
      template: "systems/trpg_tw_fr/templates/actor-sheet.html",
      width: 720,
      height: 680
    });
  }

  getData() {
    const data = super.getData();
    // Ajoutez ici les données nécessaires pour le template
    return data;
  }
}

/**
 * Feuille d'objet minimale pour The Witcher (FR)
 */
class TRPGItemSheet extends ItemSheet {
  static get defaultOptions() {
    return mergeObject(super.defaultOptions, {
      classes: ["trpg-tw-fr", "sheet", "item"],
      template: "systems/trpg_tw_fr/templates/item-sheet.html",
      width: 520,
      height: 480
    });
  }

  getData() {
    const data = super.getData();
    return data;
  }
}

// Exposer globalement pour être utilisé lors de l'init
window.TRPGActorSheet = TRPGActorSheet;
window.TRPGItemSheet = TRPGItemSheet;

export { TRPGActorSheet, TRPGItemSheet };

